/*
  Animation keyframes. These must be placed at the top of the file
  in order to work in IE.
*/
@-webkit-keyframes dot {
  0% { opacity: 0; }
  100% { opacity: 1; }
}
@-moz-keyframes dot {
  0% { opacity: 0; }
  100% { opacity: 1; }
}
@keyframes dot {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

@keyframes fadein {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

/* Firefox < 16 */
@-moz-keyframes fadein {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

/* Safari, Chrome and Opera > 12.1 */
@-webkit-keyframes fadein {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

/* Internet Explorer */
@-ms-keyframes fadein {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

/* Opera < 12.1 */
@-o-keyframes fadein {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

@-webkit-viewport {
  width: device-width;
}

@-moz-viewport {
  width: device-width;
}

@-ms-viewport {
  width: device-width;
}

@-o-viewport {
  width: device-width;
}

@viewport {
  width: device-width;
}

/* Angular material overrides. */
md-input-group.long > input {
  height: 45px;
  width: 100%;
}

label {
  display: inline-block;
  font-weight: bold;
  margin-bottom: 5px;
  max-width: 100%;
}

md-input-group.md-default-theme label {
  font-size: 16px;
  font-weight: 200;
  margin-bottom: 0;
}

/* When AngularFire is in emulator mode, it appends the following element to
the <body> of every page:

    <div class="firebase-emulator-warning"
         style="background-color: rgb(255, 255, 255);
                border: 0.1em solid rgb(0, 0, 0);
                bottom: 0px;
                color: rgb(255, 0, 0);
                left: 0px;
                margin: 0px;
                position: fixed;
                text-align: center;
                width: 100%;
                z-index: 10000;">
      Running in emulator mode. Do not use with production credentials.
    </div>
*/
.firebase-emulator-warning {
  /* Enhance the contrast of the text to improve accessibility. We need to use
   `!important` here because inline style rules have a higher priority than CSS
   style rules, but we only want these CSS rules to be respected. */
  background-color: #FFFFFF !important;
  color: #CC0000 !important;
  /* Do not allow the element to swallow pointer/mouse events. */
  pointer-events: none;
}

/* The following classes' styles are overriden by Guppy's OSK stylesheet and
they end up rotating the tooltips which are placed on the left or right. This is
why they need to be overriden.  */
.left, .right {
  -webkit-transform: none !important;
  transform: none !important;
}

.md-button {
  font-size: 16px;
  line-height: 22px;
  min-height: 22px;
  min-width: 22px;
  padding: 6px;
}

.md-button.md-default.theme {
  border-radius: 3px;
}

.md-button-success {
  color: #009688;
}

.md-shadow-bottom-z-1, .md-button.md-raised:not([disabled]),
.md-button.md-fab {
  box-shadow: 0 2px 5px 0 rgba(0,0,0,0.26);
}

.md-button.md-default-theme.md-raised:not([disabled]):hover,
.md-button.md-raised:not([disabled]):hover {
  background-color: rgba(158,158,158,0.3);
  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.4);
  transform: translate3d(0,-1px,0);
}

.oppia-learner-confirm-button.protractor-test-submit-answer-button.md-button[disabled] {
  background-color: transparent;
  color: rgba(0,0,0,0.38);
  cursor: default;
}

.oppia-learner-confirm-button.protractor-test-submit-answer-button.md-button[disabled]:hover {
  text-decoration: none;
}

.oppia-learner-confirm-button.protractor-test-submit-answer-button.md-button {
  font: 16px;
  margin: 0;
  min-width: 55px;
}

.md-button.oppia-learner-confirm-button,
.md-button.oppia-learner-got-it-button,
.md-button.oppia-learner-hint-solution-button {
  background: #0D48A1;
  color: #ffffff;
  padding: 6px 12px;
}

.md-button.oppia-learner-confirm-button:hover {
  background-color: #115fd4;
}

.md-button.oppia-learner-reload-image-button {
  background: #ffffff;
  border: 2px solid;
  border-color: #0D48A1;
  color: #0D48A1;
  padding-bottom: 12px;
  padding-left: 20px;
  padding-right: 20px;
  padding-top: 12px;
}

.md-button.md-default-theme.oppia-learner-reload-image-button:focus,
.md-button.md-default-theme.oppia-learner-reload-image-button:hover {
  background-color: rgba(224,242,241,1);
  text-decoration: none;
}

.md-button.md-default-theme.oppia-learner-confirm-button:focus,
.md-button.md-default-theme.oppia-learner-confirm-button:hover,
.md-button.md-default-theme.oppia-learner-got-it-button:focus,
.md-button.md-default-theme.oppia-learner-got-it-button:hover,
.md-button.md-default-theme.oppia-learner-hint-solution-button:focus,
.md-button.md-default-theme.oppia-learner-hint-solution-button:hover {
  background-color: #115FD4;
  text-decoration: none;
}

.btn.btn-secondary.oppia-revert-button {
  min-width: 130px;
}

.oppia-learner-got-it-button {
  margin-bottom: 25px;
  margin-right: 20px;
}

/* Attributes for material-icons. */
.material-icons {
  line-height: 1;
  vertical-align: middle;
}
.material-icons.md-18 {
  font-size: 18px;
}
.material-icons.md-32 {
  font-size: 32px;
}
.material-icons.md-40 {
  font-size: 40px;
}
.material-icons.md-100 {
  font-size: 100px;
}
.material-icons.md-dark {
  color: rgba(0, 0, 0, 0.54);
}

/* Bootstrap overrides and additions. */
html {
  background-color: #e8e7e3;
  /*
    The HTML font-size is set to 62.5% in order to make 'em' units easier to
    work with. Modern browsers set their default font-size to 16px. This means
    that 1em = 16px, which is a difficult base to work with (for example,
    1.6em = 25.6px, which is not rounded). By setting the html's font-size to
    62.5%, the base font-size becomes 10px, so 1em = 10px, and 1.6em = 16px.
    The reason for making 'em' easier to work with is that 'em's scale with
    the user's font-size preferences that they set in the browser, while
    'px' is a fixed unit of measurement that doesn't scale.
  */
  font-size: 62.5%;
  height: 100%;
}

body {
  background-color: #e8e7e3;
  color: rgba(0,0,0,0.87);
  font-family: "Roboto", Arial, sans-serif;
  /*
    A font-size of 1.6em sets the default font-size on Oppia to 16px, though
    this will scale depending on the user's font-size preferences set via
    their browser preferences.
  */
  font-size: 1.6em;
  height: 100%;
  margin: 0 auto;
  overflow-x: hidden;
  padding: 0;
  width: 100%;
}

legend {
  border-bottom: 1px solid #e5e5e5;
  font-size: 21px;
  margin-bottom: 20px;
}

.form-control {
  font-size: 14px;
  min-height: 35px;
  padding: 6px 12px 6px 12px;
}

.form-group select {
  margin-right: 5px;
}

.d-xl-inline-flex {
  width: 100%;
}

.has-error .form-control {
  border-color: #a94442;
  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
  box-shadow: inset 0 1px 1px rgba(0,0,0,.075)
}
.has-error .form-control:focus {
  border-color: #843534;
  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 6px #ce8483;
  box-shadow: inset 0 1px 1px rgba(0,0,0,.075),0 0 6px #ce8483
}

.oppia-search-bar-dropdown-menu {
  width: 170px;
}
.oppia-skip-to-content {
  left: -10000px;
  padding: 6px;
  position: absolute;
  top: auto;
  transition-duration: 1s;
  transition-property: color left;
  transition-timing-function: ease-in;
  z-index: 99999;
}
.oppia-skip-to-content:active,
.oppia-skip-to-content:hover,
.oppia-skip-to-content:focus {
  background-color: #e8e7e3;
  border-bottom: 1px solid black;
  border-bottom-right-radius: 5px;
  border-right: 1px solid black;
  color: #23527e;
  height: auto;
  left: 0;
  position: absolute;
  transition-duration: .5s;
  transition-property: color left;
  transition-timing-function: ease-out;
  width: auto;
}

.oppia-stop-scroll {
  overflow-y: hidden;
}
/* According to the Angular documentation - https://docs.angularjs.org/api/ng/directive/ngCloak -
   the following rule needs to be added to the css file in order for ng-cloak to work.  */
[ng\:cloak], [ng-cloak], .ng-cloak {
  display: none !important;
}

/* Select2 dropdown corners should not be rounded */
.select2-container .select2-choice,
.select2-container .select2-choice .select2-arrow {
  border-radius: 0;
}

/* Fix for Select2 to show full placeholder text */
.select2-selection--multiple .select2-search--inline .select2-search__field {
  width: auto !important;
}

.oppia-dashboard-container {
  margin: 0 auto 30px auto;
  max-width: 800px;
}

.oppia-learner-dashboard-icon {
  color: white;
  float: right;
  font-size: 19px;
  margin-left: -16px;
  position: relative;
  right: 13px;
  top: 13px;
  z-index: 10;
}

.oppia-dashboard-container .exp-private-text {
  color: #aaa;
  font-style: italic;
}

.oppia-activity-summary-tile.oppia-activity-playlist-tile {
  border-bottom: 1px solid #C0C0C0;
  height: 145px;
  margin: 0;
  width: 100%;
}

@media (max-width: 768px) {
  .headroom {
    transition: transform 200ms linear;
    will-change: transform;
  }

  .headroom--pinned {
    transform: translateY(0%);
  }

  .headroom--unpinned {
    transform: translateY(-100%);
  }
}

h1 {
  color: #000;
  font-size: 2em;
}

h2 {
  color: #000;
  font-size: 1.5em;
  margin: .83em 0;
}

h3 {
  color: #222;
  font-size: 1.17em;
  font-weight: bold;
  line-height: 1.1;
  margin: 18px 0 18px 0;
}

a {
  color: #0844aa;
  cursor: pointer;
}

a:hover {
  color: #3f2c76;
}

a:focus {
  outline-style: none;
}

/* Change all the borders of an input to red when there is validation errors. */
input.ng-dirty.ng-invalid, md-input-group.md-default-theme input.ng-dirty.ng-invalid {
  border-color: #F44336;
}

::-webkit-input-placeholder {
  font-style: italic;
}
::-moz-placeholder {
  font-style: italic;
}
::-ms-input-placeholder {
  font-style: italic;
}

.oppia-issues-learner-action-table td,
.oppia-issues-learner-action-table th {
  border: 1px solid black;
}

/* Change the color of odd-numbered lines in a table. */
.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th {
  background-color: #f0f0f0;
}

.oppia-form-error {
  color: red;
}

.oppia-serious-warning-text {
  color: red;
}

.oppia-disabled-contenteditable {
  background-color: #eee;
  cursor: not-allowed;
  opacity: 1;
  padding: 10px 5px;
}

p {
  line-height: 1.2;
  margin-bottom: 18px;
  text-align: start;
  word-spacing: 0;
  word-wrap: break-word;
}

p:last-child {
  margin: 0;
}

.oppia-long-text p {
  line-height: 1.4;
  /* The following should be the same as the line-height (1.4). */
  margin: 1.4em 0;
}
.oppia-dashboard-container a {
  color: black;
}
.oppia-activity-summary-tile a,
.oppia-activity-summary-tile-mobile a {
  color: inherit;
  text-decoration: none;
}
.oppia-footer-container a {
  color: #fff;
}
.oppia-sidebar-footer a {
  display: inline;
  margin-top: 5px;
  padding: 0 10px 0 0;
}
.open > a {
  outline: 0;
}
.oppia-share-dropdown-menu li > a {
  font-weight: bolder;
  padding: 10px;
  text-align: center;
}
.nav > li > a {
  display: block;
  padding: 10px 15px;
  position: relative;
}
.oppia-activity-summary-tile a:focus,
.oppia-activity-summary-tile-mobile a:focus {
  box-shadow: 0 6px 13px rgba(0,0,0,0.17), 0 4px 6px rgba(0,0,0,0.23);
}
.oppia-dashboard-container a:hover,
.oppia-dashboard-container a:active,
.oppia-dashboard-container a:visited {
  color: black;
}
.oppia-activity-summary-tile a:hover,
.oppia-activity-summary-tile-mobile a:hover,
.oppia-activity-summary-tile a:active,
.oppia-activity-summary-tile-mobile a:active,
.oppia-activity-summary-tile a:visited,
.oppia-activity-summary-tile-mobile a:visited {
  color: inherit;
  text-decoration: none;
}

.oppia-sidebar-footer a:hover {
  background: none;
}

.oppia-navbar-nav .open > a {
  background-color: #fff;
  color: #009688;
}
.oppia-dashboard-container a:hover {
  text-decoration: underline;
}

.oppia-navbar-tabs > li:hover > a {
  background-color: #fff;
  color: #009688;
}
.oppia-activity-summary-tile span {
  vertical-align: middle;
}

/* This will prevent the enlargement of the text in the RTE. A span tag gets
   introduced when a user selects the text from the end of first line, goes to the
   second one and then writes some text to replace that. The span tag carries
   along with it a style=font-size:1.6em;, due to which the text is enlarged.
   This size seems to be taken from the font-size from the body element's
   CSS class, but we do not know what JS code is causing it to be inlined in the
   span tag. There is as such no way to remove !important as of now, it's a
   CKEditor bug (issue #6206 on Oppia). */
.oppia-rte p span {
  font-size: 1em !important;
}

.oppia-rte-editor {
  border: none;
  outline: none;
}

.oppia-rte-editor p span {
  font-size: 1em !important;
}

.oppia-rte p {
  line-height: 1.2;
  margin-bottom: 18px;
  margin-top: 0px;
}

.oppia-rte ol, .oppia-rte ul, .oppia-rte-editor ol, .oppia-rte-editor ul {
  line-height: 1.846;
  margin-bottom: 18px;
}

.oppia-long-text-image {
  display: block;
  margin-left: auto;
  margin-right: auto;
  width: 80%;
}

#wrapper {
  min-height: 100%;
  position: relative;
}

.label, .badge {
  margin: auto 4px;
}
.label-default, .badge-default {
  background-color: #888;
}
.label-info, .badge-info {
  background-color: #18447E;
}
.label-success, .badge-success {
  background-color: #009688;
}

/*
  The following rule ensures that, as the viewport gets
  smaller, any media will scale down according to its
  container's width.
*/
img, video, canvas {
  max-width: 100%;
}

textarea {
  width: 90%;
}

/*
  Styles for the development mode indicator
*/
.oppia-dev-mode {
  background-color: DarkSlateGray;
  border-bottom-right-radius: 0.5em;
  border-top-right-radius: 0.5em;
  bottom: 100px;
  color: white;
  left: 0;
  opacity: 0.85;
  padding: 4px 8px;
  /* This allows clicks to 'pass through' the element. */
  pointer-events: none;
  position: fixed;
  z-index: 100;
}

/*
  Styles for BackgroundMaskService.
*/
.oppia-background-mask {
  background: rgba(0,0,0,0.33);
  height: 100vh;
  left: 0;
  position: fixed;
  top: 0;
  width: 100vw;
  z-index: 30;
}

/*
  Styles for the global navigation sidebar menu.
*/
.oppia-base-container {
  height: 100%;
  margin-left: auto;
  margin-right: auto;
  min-height: 100%;
  position: relative;
}

/*
  Style for the oppia reload symbol in reload image button.
*/
.oppia-reload-image-symbol {
  padding-bottom: 10px;
  padding-left: 16px;
  position: relative;
}

/*
  Note that adding "overflow-y: scroll;" will break infinite scrolling in the
  search results page.
*/
.oppia-content-container {
  height: 100%;
  left: 0;
  position: relative;
  -webkit-transition: -webkit-transform 0.5s;
  transition: transform 0.5s;
}

.oppia-correctness-label-editor {
  margin-left: 4px;
  margin-top: 18px;
}
.oppia-sidebar-menu-open .oppia-sidebar-menu {
  box-shadow: 1px 0 3px rgba(0,0,0,0.12), 1px 0 2px rgba(0,0,0,0.24);
  height: 110vh;
  overflow-y: scroll;
  -ms-transform: translate(0, 0);
  -webkit-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
  visibility: visible;
}
.oppia-sidebar-menu-open .oppia-sidebar-menu::after {
  height: 0;
  opacity: 0;
  -webkit-transition: opacity 0.5s, width 0.1s 0.5s, height 0.1s 0.5s;
  transition: opacity 0.5s, width 0.1s 0.5s, height 0.1s 0.5s;
  width: 0;
}

.oppia-sidebar-menu-open .oppia-content-container::after {
  height: 100%;
  opacity: 1;
  -webkit-transition: opacity 0.5s;
  transition: opacity 0.5s;
  width: 100%;
}
.oppia-sidebar-menu-closed .oppia-content-container::after {
  opacity: 0;
  -webkit-transition: opacity 0.5s;
  transition: opacity 0.5s;
}

.oppia-sidebar-logo {
  height: 32px;
  margin-bottom: 5px;
  margin-left: 15px;
}

.oppia-sidebar-footer {
  bottom: 10px;
  color: #777;
  left: 25px;
  position: absolute;
}

.oppia-sidebar-footer div {
  margin-bottom: 10px;
}

.oppia-footer {
  background-color: #094142;
  bottom: 0;
  color: #fff;
  font-size: 13px;
  line-height: 20px;
  padding-bottom: 5vh;
  position: relative;
  width: 100%;
}

.oppia-footer-container {
  margin-left: auto;
  margin-right: auto;
  max-width: 800px;
  padding: 30px 80px;
}

.oppia-navbar-nav {
  float: left;
}
.nav-link {
  display: block;
  font-size: 14px;
  position: relative;
}
.oppia-navbar-nav > .nav-link {
  padding: 10px 5px;
}
.oppia-same-row-container {
  float: left;
}
.oppia-navbar-dropdown .nav-link {
  color: #009688;
}
.modal-body .nav-link {
  color: #00609C;
  font-size: 16px;
  font-weight: bold;
}
.oppia-navbar-dropdown .nav-link:hover {
  color: #888;
}



.input-group-addon.oppia-search-bar-icon {
  border-radius: 4px 0 0 4px;
  font-size: 14px;
  font-weight: normal;
  line-height: 1;
  padding: 7px 12px;
  text-align: center;
}
.oppia-help-dropdown .uib-popover {
  color: white;
  font-size: 14px;
  max-width: 200px;
  text-align: center;
}
.oppia-help-dropdown .popover-inner {
  max-width: 140px;
}
/*
  Styles for footer
*/

.oppia-footer-container h4 {
  font-size: 1em;
  font-weight: bold;
  margin: 1.33em 0;
  text-transform: uppercase;
}

.oppia-footer-container ul {
  padding-left: 0;
}

.oppia-footer-container li {
  list-style-position: inside;
  list-style-type: none;
}

.oppia-language-selector:focus {
  outline: 1px dotted #FFF;
  outline: auto 5px -webkit-focus-ring-color;
}
.oppia-footer-padding {
  height: 198px;
}

.oppia-footer-padding-below-banner {
  height: 300px;
}

@media (max-width: 768px) {
  .oppia-footer-padding {
    height: 554px;
  }
  .oppia-footer-container {
    padding: 30px;
  }

}
@media only screen and (min-width: 700px) and (max-width: 768px) {
  .oppia-footer-padding-below-banner {
    height: 270px;
  }
}
@media (max-width: 440px) {
  .oppia-footer-padding-below-banner {
    height: 270px;
  }
}
/*
  This is needed for proper display of tabs. See
  https://github.com/angular-ui/bootstrap/commit/8620aedba99b05822311
*/
.navbar-nav, .uib-pagination {
  cursor: pointer;
}

.navbar {
  border: 0;
  margin-bottom: 0;
  padding: 0;
  width: 100%;
}
.navbar-form {
  padding-left: 15px;
}

.nav {
  list-style: none;
  margin-bottom: 0;
  padding-left: 0;
}

.nav > li {
  display: block;
  position: relative;
}

.dropdown-menu {
  -webkit-background-clip: padding-box;
  background-clip: padding-box;
  -webkit-box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
  box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
  font-size: 14px;
  margin: 2px 0 0;
  max-width: 220px;
  min-width: 160px;
  padding: 5px 0;
}

.open > .dropdown-menu {
  display: block;
}

.float-right > .dropdown-menu {
  left: auto;
  right: 0;
}
.dropdown-item.active, .dropdown-item:active {
  background-color: transparent;
}
.oppia-navbar-profile-picture-container .caret {
  margin: 4px;
  margin-left: 10px;
}
.dropup .caret,
.navbar-fixed-bottom .dropdown .caret {
  border-bottom: 4px solid;
  border-top: 0;
  content: "";
}
.dropup .dropdown-menu,
.navbar-fixed-bottom .dropdown .dropdown-menu {
  bottom: 100%;
  margin-bottom: 2px;
  top: auto;
}
.nav-item {
  display: block;
  position: relative;
}

.nav-link.dropdown-item {
  padding: 3px 20px 2px 20px;
}
