.oppia-on-off-switch-checkbox {
  display: none;
}
.oppia-on-off-switch-label {
  border: 2px solid #999;
  border-radius: 20px;
  cursor: pointer;
  display: block;
  overflow: hidden;
}
.oppia-on-off-switch-inner {
  display: block;
  margin-left: -100%;
  transition: margin 0.3s ease-in 0s;
  width: 200%;
}
.oppia-on-off-switch-inner:before, .oppia-on-off-switch-inner:after {
  box-sizing: border-box;
  color: white;
  display: block;
  float: left;
  font-family: "Capriola", "Roboto", Arial, sans-serif;
  font-size: 12px;
  font-weight: bold;
  height: 20px;
  line-height: 20px;
  padding: 0;
  width: 50%;
}
.oppia-on-off-switch-inner:before {
  background-color: #04857c;
  color: #fff;
  content: "ON";
  padding-left: 10px;
}
.oppia-on-off-switch-inner:after {
  background-color: #eee;
  color: #999;
  content: "OFF";
  padding-right: 10px;
  text-align: right;
}
.oppia-on-off-switch-main {
  background: #fff;
  border: 2px solid #999;
  border-radius: 20px;
  bottom: 0;
  display: block;
  height: 17px;
  margin: 3.5px;
  position: absolute;
  right: 41px;
  top: 0;
  transition: all 0.3s ease-in 0s;
  width: 18px;
}
.oppia-on-off-switch-checkbox:checked + .oppia-on-off-switch-label .oppia-on-off-switch-inner {
  margin-left: 0;
}
.oppia-on-off-switch-checkbox:checked + .oppia-on-off-switch-label .oppia-on-off-switch-main {
