/*
  NOTE to developers: We generate RTL CSS from this file in the webpack
  compilation, here are sme additional rules that can be added to the CSS files:
  https://rtlcss.com/learn/usage-guide/control-directives .
*/

.about-page .carousel-indicators li {
  background-color: gray;
  border-radius: 50%;
}

.about-page .carousel-indicators li.active {
  background-color: #005240;
}

.about-page .oppia-about-hero-section {
  align-items: end;
  background-image: url('/assets/images/about/about-banner-desktop.webp');
  background-repeat: no-repeat;
  background-size: cover;
  display: flex;
  flex-direction: row;
  height: 31em;
  padding: 0 64px 48px;
}

.about-page .oppia-about-hero-section-content {
  background-color: #ffffffed;
  border-radius: 4px;
  padding: 48px 32px;
  width: 58%;
}

.about-page .oppia-about-hero-title {
  color: #00645c;
  font-family: "Capriola", "Roboto", Arial, sans-serif;
  font-size: 4.8rem;
  line-height: 60px;
  margin: 0;
}

.about-page .oppia-about-hero-subtext {
  color: #333;
  font-family: "Roboto", Arial, sans-serif;
  font-size: 20px;
  line-height: 27px;
  margin: 8px 0 0;
}

@media only screen and (max-width: 901px) {
  .about-page .oppia-about-hero-section {
    height: 18em;
    padding: 0 16px 32px;
  }

  .about-page .oppia-about-hero-section-content {
    padding: 24px 32px;
    width: 90%;
  }

  .about-page .oppia-about-hero-title {
    font-size: 3rem;
    line-height: 37.5px;
  }

  .about-page .oppia-about-hero-subtext {
    font-size: 1.6rem;
    line-height: 22.4px;
    width: 90%;
  }
}

@media only screen and (max-width: 641px) {
  .about-page .oppia-about-hero-section {
    height: 18em;
    padding: 0 24px 16px;
  }

  .about-page .oppia-about-hero-section-content {
    padding: 16px 24px;
    width: 77%;
  }

  .about-page .oppia-about-hero-subtext {
    width: 100%;
  }
}

@media only screen and (max-width: 481px) {
  .about-page .oppia-about-hero-section {
    background-image: url('/assets/images/about/about-banner-mobile.webp');
    height: 18em;
    padding: 0 12px 48px;
  }
  .about-page .oppia-about-hero-section-content {
    padding: 10px 16px;
    width: 77%;
  }
  .about-page .oppia-about-hero-title {
    font-size: 2rem;
    line-height: 25px;
  }
  .about-page .oppia-about-hero-subtext {
    display: none;
  }
}

.about-page .oppia-about-foundation-section {
  background-color: #fff;
  column-gap: 7.1vw;
  display: flex;
  flex-direction: row-reverse;
  height: fit-content;
  justify-content: space-between;
  padding: 112px 64px;
}

.about-page .oppia-about-foundation-section-right {
  display: flex;
  flex-direction: column;
  min-width: 57vw;
  row-gap: 3.2rem;
}

.about-page .oppia-about-foundation-section-products {
  border: 1px solid #bfd8d7;
  border-radius: 8px;
  padding: 3.2rem 4.8rem;
}

.about-page .oppia-about-title-new {
  color: #00645c;
  font-family: "Capriola", "Roboto", Arial, sans-serif;
  font-size: 3rem;
  line-height: 3.6rem;
  margin-bottom: 0;
}

.about-page .oppia-about-foundation-section  .oppia-about-title-new {
  margin-bottom: 16px;
}

.about-page .oppia-about-foundation-section-goals-container {
  display: flex;
  flex-direction: row;
  gap: 3.2rem;
  justify-content: space-between;
}

.about-page .oppia-about-foundation-section-goal {
  background-color: #e3f1f1;
  border-radius: 8px;
  display: flex;
  flex-direction: column;
  padding: 4.8rem;
  row-gap: 8px;
}

.about-page .oppia-about-foundation-section-goal-title {
  color: #00645c;
  font-family: "Capriola", "Roboto", Arial, sans-serif;
  font-size: 20px;
}

.about-page .oppia-about-foundation-section-left {
  align-self: end;
  display: flex;
  flex-direction: column;
}

.about-page .oppia-about-foundation-section-timeline-circle {
  border: 1.5px solid #d2ac61;
  border-radius: 50%;
  height: 15px;
  margin: 20px 0 20px -6px;
  width: 15px;
}

.about-page .oppia-about-foundation-section-timeline-subtitle {
  color: #717171;
  font-family: "Capriola", "Roboto", Arial, sans-serif;
  font-size: 2rem;
  line-height: 25px;
  margin-left: 1rem;
}

.about-page .oppia-about-foundation-section-timeline-item {
  border-left: 2.5px solid #00645c;
  display: flex;
  flex-direction: column;
  padding: 0 1rem;
  row-gap: 8px;
}

.about-page .oppia-about-foundation-section-timeline-year {
  color: #000;
  font-family: "Capriola", "Roboto", Arial, sans-serif;
  font-size: 2rem;
  line-height: 24px;
  margin: 0;
}

.about-page .oppia-about-section-subtext-new {
  color: #333;
  font-family: "Roboto", Arial, sans-serif;
  font-size: 18px;
  line-height: 21.6px;
}

.about-page .oppia-about-partnerships-section {
  background-color: #fff;
  padding: 0 0 120px;
  width: 100%;
}

.about-page .oppia-about-partnerships-top {
  display: flex;
  flex-direction: column;
  gap: 16px;
  padding: 0 64px 32px;
}

.about-page .oppia-about-subtitle.oppia-about-partnerships-card-title {
  margin: 0 0 8px;
}

.about-page .oppia-about-partnerships-cards-container-tablet {
  display: none;
}

.about-page .oppia-about-partnerships-cards-container-desktop {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  width: 100%;
}

.about-page .oppia-about-partnerships-card {
  display: flex;
  flex-direction: column;
  gap: 16px;
  padding: 64px;
  width: 50%;
}

.about-page .oppia-about-partnerships-card:nth-child(1),
.about-page .oppia-about-partnerships-card:nth-child(4) {
  background-color: #f5f9f9;
}

.about-page .oppia-about-partnerships-card:nth-child(2),
.about-page .oppia-about-partnerships-card:nth-child(3) {
  background-color: #fcfcfd;
}

.about-page .oppia-about-partnerships-card-label {
  font-weight: 300;
}

.about-page .oppia-about-partnerships-card-label span {
  color: #000;
  font-weight: 500;
}

.about-page .oppia-about-partnerships-card:nth-child(4) .oppia-about-section-subtext-new span {
  font-weight: 700;
}

.about-page .oppia-about-partnerships-card-bottom {
  display: flex;
  flex-direction: row;
  gap: 24px;
}

.about-page .oppia-about-partnerships-card-image-container {
  min-height: 165px;
  min-width: 55%;
}

.about-page .oppia-about-partnerships-card-image {
  height: 100%;
  width: 100%;
}

.about-page .oppia-about-partnerships-card-image img {
  height: 100%;
  width: 100%;
}

@media only screen and (max-width: 884px) {
  .about-page  .oppia-about-partnerships-section br {
    display: none;
  }
  .about-page .oppia-about-partnerships-top {
    gap: 8px;
    padding: 0 48px 16px;
  }
  .about-page .oppia-about-partnerships-cards-container-desktop {
    display: none;
  }
  .about-page .oppia-about-partnerships-cards-container-tablet {
    background-color: #f5f8f8;
    display: flex;
    padding: 48px 80px 80px;
  }
  .about-page .oppia-about-partnerships-card {
    display: flex;
    flex-direction: row-reverse;
    gap: 16px;
    padding: 0;
    width: 100%;
  }
  .about-page .oppia-about-partnerships-cards-container-tablet .oppia-about-partnerships-card {
    background-color: #f5f8f8;
  }
  .about-page .oppia-about-partnerships-card-label {
    margin-bottom: 8px;
  }
  .about-page .oppia-about-partnerships-card-image-container {
    max-height: 162px;
    min-width: 37%;
  }
